function ifScript() {
	if (workflow.scratchpad.haltString == "") {
		return 'yes';     
	}
	return 'no';
}

try {

	gs.info("IBM Firewall Atuomation XML: Rule Build Activity");
	gs.info("IBM Firewall Atuomation: Validating Rules");
	for (var i = 0; i < workflow.scratchpad.rules.length; i++) {
		gs.info("IBM Firewall Atuomation XML Rule #" + i + "Source: " + workflow.scratchpad.rules[i].sourceIPSet + " Destination: " + workflow.scratchpad.rules[i].destinationIPSet + " Service: " + workflow.scratchpad.rules[i].service + " Apply To Device: " + workflow.scratchpad.rules[i].applyToDevice + " Log: " + workflow.scratchpad.rules[i].log + " Section ID: " + workflow.scratchpad.rules[i].sectionID + " DFW: " + workflow.scratchpad.rules[i].dfw + " Action: " + workflow.scratchpad.rules[i].action);  
	}

	workflow.scratchpad.errorString += "Rules Length: " + workflow.scratchpad.rules.length + " ::";
	for (var i = 0; i < workflow.scratchpad.rules.length; i++) {
		gs.info("IBM Firewall Atuomation XML: Rule #" + (i+1));
		var eTag = "";
		var endPoint = 'https://' + workflow.scratchpad.nsx_manager + '/api/4.0/firewall/globalroot-0/config/layer3sections/' + workflow.scratchpad.rules[i].sectionID;
		var ipSetXML = "";

		var resp = NSXRESTCall(endPoint, "GET", "", "", "application/json", "application/xml");
		eTag = resp.getHeader('ETag');
		gs.info("IBM Firewall Atuomation XML: " + eTag);

		var fw = "", src = "", dst = "", srv = "";
		if (workflow.scratchpad.rules[i].dfw == "true") {
			fw = "<appliedTo><value>DISTRIBUTED_FIREWALL</value><type>DISTRIBUTED_FIREWALL</type></appliedTo>";
			if (workflow.scratchpad.rules[i].applyToDevice != '') {
				fw += "<appliedTo><value>" + workflow.scratchpad.rules[i].applyToDevice + "</value><type>Edge</type></appliedTo>";
			}
		}
		else {
			if (workflow.scratchpad.rules[i].applyToDevice != '') {
				fw += "<appliedTo><value>" + workflow.scratchpad.rules[i].applyToDevice + "</value><type>Edge</type></appliedTo>";
			}
		}

		if (workflow.scratchpad.rules[i].sourceIPSet != '') {
			src = "<sources excluded=\"false\"><source><value>" + workflow.scratchpad.rules[i].sourceIPSet + "</value><type>IPSet</type></source></sources>";
		}
		if (workflow.scratchpad.rules[i].destinationIPSet != '') {
			dst = "<destinations excluded=\"false\"><destination><value>" + workflow.scratchpad.rules[i].destinationIPSet + "</value><type>IPSet</type></destination></destinations>";
		}
		if (workflow.scratchpad.rules[i].service != '') {
			srv = "<services><service><value>" + workflow.scratchpad.rules[i].service + "</value></service></services>";
		}

		var now = new GlideDateTime();
		ipSetXML = "<rule disabled=\"false\" logged=\"" + workflow.scratchpad.rules[i].log + "\"><name>" + workflow.scratchpad.rules[i].description + "_" + current.number + "_Automated_" + now + "</name><action>" + workflow.scratchpad.rules[i].action + "</action><notes/><appliedToList>" + fw + "</appliedToList><sectionId>" + workflow.scratchpad.rules[i].sectionID + "</sectionId>" + src + dst + srv + "</rule>";
		
		workflow.scratchpad.generatedXML += "Rule # " + (i+1) + " \n\n";
		workflow.scratchpad.generatedXML += ipSetXML;
		workflow.scratchpad.generatedXML += "\n\n";

		endPoint = 'https://' + workflow.scratchpad.nsx_manager + '/api/4.0/firewall/globalroot-0/config/layer3sections/' + workflow.scratchpad.rules[i].sectionID + '/rules';

		resp = NSXRESTCall(endPoint, "POST", ipSetXML, eTag, 'application/json', 'application/xml');
		if (resp.getStatusCode() != 201 && resp.getStatusCode() != 200) {
			workflow.scratchpad.haltString += response.getErrorMessage();
		}
		workflow.scratchpad.errorString += endPoint;
	}
}
catch (err) {
	workflow.scratchpad.haltString += err;
}

