function createSets(dfw, description, src_address, dst_address, port, protocol, applyToDevice, log, action, section) {
	
	gs.info("IBM Firewall Automation: Read Out Of Parameters For Set Creation: Source Address: " + src_address + " : Destination Address: " + dst_address + " : Port : " + port + " : Protocol : " + protocol + " :Apply To Device: " + applyToDevice + " : Log : " + log + " : action : " + action + " : section : " + section); 
	var srcIpSet = "";
	var dstIpSet = "";
	var appService = "";
	if (src_address != '')               srcIpSet   = ""+testInputAgainstIPSets(src_address);
	if (dst_address != '')               dstIpSet   = ""+testInputAgainstIPSets(dst_address);
	if (protocol    != '' && port != '') appService = ""+testInputAgainstServices(port, protocol);

	var dfwi = ""+dfw;
	var desc = ""+description;
	var apply = ""+applyToDevice;
	var lo = ""+log;
	var act = ""+action;
	var sect = ""+section;
	
	gs.info("IBM Firewall Automation: Read Out Of Parameters For Set Creation: Source Address: " + src_address + " : Destination Address: " + dst_address + " : Port : " + port + " : Protocol : " + protocol + " :Apply To Device: " + apply + " : Log : " + lo + " : action : " + act + " : section : " + sect); 
	
	var fireWall = new FirewallRule(dfwi, srcIpSet, dstIpSet, appService, apply, lo, act, desc, sect);
	workflow.scratchpad.rules.push(JSON.parse(JSON.stringify(fireWall)));
	
	gs.info("IBM Firewall Automation: Rule workflow.scratchpad.current Length " +  workflow.scratchpad.rules.length);
	
}

function testInputAgainstIPSets(srcAddressNetwork) {
	gs.info("IBM Firewall Automation: TestInputAgainstServices " + srcAddressNetwork);
	name = "AutomatedIPSet_"+srcAddressNetwork;
	for (var i = 0; i < ipSetsFromNSX.length; i++) {
		if (srcAddressNetwork == ipSetsFromNSX[i].value || name == ipSetsFromNSX[i].name) {
			gs.info("IBM Firewall Automation: Found IPSet: " + ipSetsFromNSX[i].objectId);
			return ipSetsFromNSX[i].objectId;
		}
	}
	
	var ipSetName = 'AutomatedIPSet_' + srcAddressNetwork;
	var ipSetXML = "<ipset><objectId/><type><typeName/></type><description>" + ipSetName + "</description><name>" + ipSetName + "</name><revision>0</revision><objectTypeName/><value>" + srcAddressNetwork + "</value></ipset>";
	
	workflow.scratchpad.generatedXML += "IPSet Generated: \n\n";
	workflow.scratchpad.generatedXML += ipSetXML;
	workflow.scratchpad.generatedXML += "\n\n";
	
	var response = NSXRESTCall('https://' + workflow.scratchpad.nsx_manager + '/api/2.0/services/ipset/globalroot-0', 'POST', ipSetXML, '', 'application/json', 'application/xml'); 

	if (response.getStatusCode() == 201 || response.getStatusCode() == 200) {
		gs.info("IBM Firewall Automation: Created IPSet!");
		var req = response.getBody();
		gs.info("IBM Firewall Automation: IPSet ID: " + req);
		return req;
	}
	else {
		gs.info("IBM Firewall Automation: Error creating IPSet " + response.getStatusCode());
		workflow.scratchpad.haltString += response.getStatusCode + " : " + response.getErrorMessage();
	}
}

